import FloorView from '../basic/floorView'
/**
 * 二维坐标系以左上角为原点
 * 三维坐标系以俯视图左上角为原点，Z轴指向屏幕外
 * width：俯视图中的宽（X轴）
 * height：俯视图中的高（Y轴）
 * depth：正视图中的高（Z轴）
 */
export default {
  name: 'ThreeView',
  mounted() {
    const floorView = new FloorView({
      container: '#root',
      width: 1500,
      height: 1500,
      parts: [
        {
          image: 'beam_3.svg',
          relWidth: 100,
          relHeight: 200,
          transfer: {
            x: 100,
            y: 200,
          },
        },
        {
          image: 'beam_3.svg',
          relWidth: 50,
          relHeight: 100,
          transfer: {
            x: 100,
            y: 200,
          },
        },
      ],
    })

    floorView.render()

    var title = this.readFile('../assets/beam_1.svg')
    console.log(title)
  },
  methods: {
    readFile(filePath) {
      // 创建一个新的xhr对象
      let xhr = null
      if (window.XMLHttpRequest) {
        xhr = new XMLHttpRequest()
      } else {
        xhr = new ActiveXObject('Microsoft.XMLHTTP')
      }
      const okStatus = document.location.protocol === 'file' ? 0 : 200
      xhr.open('GET', filePath, false)
      xhr.overrideMimeType('image/svg+xml')
      xhr.send(null)
      return xhr.status === okStatus ? xhr.responseText : null
    },
  },
}
